import React from "react";
import './App.css';
import './galleryView.css';
import axios from 'axios';


const api = axios.create({
    baseURL: 'https://api.artic.edu/api/v1/'
})

class Results extends React.Component<{}, { items: Array<any>, loaded:boolean, count:number,page:number}> {
    constructor(props:any) {
        super(props);
        this.state = {
            items: [],
            loaded: false,
            count:0,
            page:1
        }
    }

    componentDidMount() {
        api.get("https://api.artic.edu/api/v1/artworks?page=1&limit=33")
            .then((response) => {
                this.setState({
                    items: response.data,
                    loaded: true,
                    count:0
                });
            })
    }
    componentWillUnmount() {
        // All clean up should come to here
    }

    componentDidUpdate() {
        // After any update
        // this.setState({loaded:false})
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        }
        this.fetchData(this.state.page)
        // alert(this.state.page)

    }

    fetchData(pageNum:any) {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        }
        axios.get("https://api.artic.edu/api/v1/artworks?page="+pageNum+"&limit=33")
            .then((response) => {

                const temp: string[] = []
                if (response.data) {

                    response.data.data.map( (item:any) => (
                        temp.push(item)
                    ))

                    this.setState({
                        items:temp
                    })
                }

                // this.setState({
                //     items: response.data,
                //     loaded: true,
                //     count:0
                // });
            })
    }

    onError(event:any) {
        const target = event.target as HTMLImageElement;
        target.style.display= 'none';
    }

    getImage(url:any, id:any) {
        // if id is empty
        // if(id === null) {
        //     return ''
        // }
        return url + "/" + id +"/full/843,/0/default.jpg";
    }

    render() {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        } 
        else if (this.state.items.length === 0) {
            return <div> <h1> Loading </h1> </div> ;
        }
        else {
            return (
                <div className = "App">

                    <h1 className="title"> Artwork List </h1>  
                    <div className="box">
                    {   
                this.state.items.map((item:any) => ( 
                    <ol key = { item.id } className ="egg">
                        {/* {"-"+item.image_id+"-"} */}
                        <div className="word">{item.title}</div>
                        <img src={this.getImage("https://www.artic.edu/iiif/2/", item.image_id)} className="helper" alt ="source unavailable" />
                        </ol>
                    ))

            }

                    </div>
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page-1}))}>Last page</button>
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page+1}))}>Next page</button>
                </div>
            );
        }
    }
}

export default Results;
