import React from "react";
import './App.css';
import './galleryView.css';
import axios from 'axios';

interface Item {
    id: number,
    image_id: string,
    title: string
}

// const createElement = React.createElement('hh', {className: "a"}, "Yes")
class SearchBar extends React.Component<{}, { items: Array<Item>,loaded:boolean, count:number,page:number,value:string,curr:string,order:string,sortType:string}> {
    constructor(props:any) {
        super(props);
        this.state = {
            page:1,
            value: '',
            items: [],
            loaded: false,
            count:0,
            curr:'',
            order:"ASC",
            sortType:"date_end"
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSelect = this.handleSelect.bind(this);
    }

    fetchData() {
        axios.get("https://api.artic.edu/api/v1/artworks/search?q="+this.state.value
        +"&fields=id,title,image_id&size=33&page="+this.state.page
        +"&sort["+ this.state.sortType
        +"][order]="+this.state.order)
            .then((response) => {
                const tmp: any[] = []

                if (response.data) {
                    response.data.data.map( (item:Item) => (
                        tmp.push(item)
                    ))
                    this.setState({
                        items: tmp,
                        loaded: true,
                        count:0
                    })
                }
            })
    }



    componentDidMount() {
        this.fetchData()
    }

    componentDidUpdate() {

        this.fetchData()  
        // alert(this.state.sortType)      
    }


    getImage(url:any, id:any) {
        // if id is empty
        if(id === null) {
            return 'https://www.nutraingredients-usa.com/var/wrbm_gb_food_pharma/storage/images/9/4/5/8/218549-6-eng-GB/Akay-Flavours-Aromatics-Pvt.-Ltd2.jpg'
        }
        return url + "/" + id +"/full/843,/0/default.jpg";
    }

    handleChange(event:any) {
        this.setState({value: event.target.value})
    }

    onChangeValue(event:any) {
        this.setState({sortType: event.target.value})
    }

    handleSubmit(event:any) {

    }

    handleSelect(event:any) {
        alert(this.state.sortType)      
        this.setState({sortType: event.target.value})
    }

    render() {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        } else { return (
            <div className = "App">
                { this.state.sortType}
            <form onSubmit = {this.handleSubmit}>
            <label>
                <div className="word3">Search Your favourite artworks</div>

                <p/>

                <input
                    type = "text"
                    value = {this.state.value}
                    onChange = {this.handleChange} className ="egg2"/>
                {/* <input type ="submit" value ="Clear" className ="egg3"/> */}
            </label>
            <br></br>
            <div className="box3">
            <div className="word3">Sort by:</div>
            <select name="selectList" id="selectList" className="egg5" onChange={this.handleSelect}>
                  <option value="date_end">Date Painted</option>
                  <option value="artwork_type_id">Art Work Type</option>
                <option value="artist_id">Artist ID</option>

                </select>
            </div>


            <div className="box3">

            <div >
                <input type="radio" value="ASC" onChange={this.onChangeValue} /> ASC
                <input type="radio" value="DESC" onChange={this.onChangeValue}  /> DESC
            </div>

            </div>

            </form>



                <hr/>

            <div className="box">
            {   
                this.state.items.map((item:any) => ( 
                    <div className ="egg">
                        <div className="word">{item.title}</div>
                        <img src={this.getImage("https://www.artic.edu/iiif/2/", item.image_id)} className="helper" alt ="source unavailable" 

                        />
                        </div>
                    ))

            }
            </div>
            <div className="box2">
            <button onClick = {()=>(this.setState({loaded:false, page:this.state.page-1}))} className="buttonsize">Last page</button>
            <button onClick = {()=>(this.setState({loaded:false, page:this.state.page+1}))} className="buttonsize">Next page</button>
            </div></div>


        )

        }

    }
}

export default SearchBar;
