import React, { useState,useEffect } from 'react';
import './App.css';
import './galleryView.css';
import axios from 'axios';
import { useLocation } from 'react-router-dom'

interface Item {
    id: number,
    image_id: string,
    date_display:number,
    title: string
}

export default function Detail(s:any) {
    const location = useLocation();
    const loc_cut = location.pathname.substring(9);

    const [state, setState] = useState([] as any)
    useEffect(() => {
    axios.get("https://api.artic.edu/api/v1/artworks/search?query[term][id]="+loc_cut)
            .then((response) => {
                const tmp: any[] = []
                if (response.data) {
                    response.data.data.map( (item:Item) => (

                        tmp.push(item)
                    ))
                    setState({
                        tmp
                    })
                    }

                }

            )
        })

        // getImage(url:any, id:any) {
        //         // if id is empty
        //         if(id === null) {
        //             return 'https://www.nutraingredients-usa.com/var/wrbm_gb_food_pharma/storage/images/9/4/5/8/218549-6-eng-GB/Akay-Flavours-Aromatics-Pvt.-Ltd2.jpg'
        //         }
        //         return url + "/" + id +"/full/843,/0/default.jpg";
        //     }

    alert(tmp.length)
    return(
        <div className="box">
            {   
                tmp.map((item:any) => ( 

                        <div className="word">{item.title}</div>


                    ))

            }
            </div>

    )
};




