import React from "react";
import './App.css';
import './showResult.css';
import axios from 'axios';

class Results extends React.Component<{}, { items: [],loaded:boolean, count:number,page:number}> {
    constructor(props:any) {
        super(props);
        this.state = {
            items: [],
            loaded: false,
            count:0,
            page:1
        }
    }

    componentDidMount() {
        axios.get("https://api.artic.edu/api/v1/artworks?page=1&limit=33")
            .then((response) => {
                this.setState({
                    items: response.data,
                    loaded: true,
                    count:0
                });
            })
    }
    componentWillUnmount() {
        // All clean up should come to here
    }

    componentDidUpdate() {
        // After any update
        // this.setState({loaded:false})
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        }
        this.fetchData(this.state.page)
        // alert(this.state.page)

    }

    fetchData(pageNum:any) {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        }
        axios.get("https://api.artic.edu/api/v1/artworks?page="+pageNum+"&limit=33")
            .then((response) => {
                this.setState({
                    items: response.data,
                    loaded: true,
                    count:0
                });
            })
    }

    onError(event:any) {
        const target = event.target as HTMLImageElement;
        target.style.display= 'none';
    }

    getImage(url:any, id:any) {
        // if id is empty
        // if(id === null) {
        //     return ''
        // }
        return url + "/" + id +"/full/843,/0/default.jpg";
    }

    render() {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        } else {
            return (
                <div className = "App">

                    <h1 class="title"> Artwork List </h1>  
                    <div class="box">
                    {   
                        this.state.items.map((item:any) => ( 
                            <ol key = { item.id } class ="egg">
                                {/* {"-"+item.image_id+"-"} */}
                                <div class="word">{item.title}</div>
                                <img src={this.getImage(this.state.items.config.iiif_url, item.image_id)} class="helper" alt ="source unavailable" 
                                        onerror={(event)=>onerror(event)}/>
                                </ol>
                            // ()=>{
                            //     if(true) {
                            //         <ol key = { item.id } class ="egg">
                            //             <img src={this.getImage(this.state.items.config.iiif_url, item.image_id)} class="helper"/>
                            //             </ol>
                            //     }
                            // }
                            ))

                    }
                    </div>
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page-1}))}>Last page</button>
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page+1}))}>Next page</button>
                </div>
            );
        }
    }
}

export default Results;
