import React from "react";
import './App.css';
import './galleryView.css';
import axios from 'axios';
import { Link} from 'react-router-dom';

interface Item {
    id: number,
    image_id: string,
    date_display:number,
    title: string
}


class Results extends React.Component<{}, { items: Array<any>, loaded:boolean, count:number,page:number,start:number,end:number}> {
    constructor(props:any) {
        super(props);
        this.state = {
            items: [],
            loaded: false,
            count:0,
            page:1,
            start:1000,
            end:2030
        }
        this.handleOnError = this.handleOnError.bind(this);
    }

    fetchData() {
        axios.get("https://api.artic.edu/api/v1/artworks/search?query[range][date_end][gte]="+this.state.start
            +"&query[range][date_end][lt]=" +this.state.end
            +"&fields=id,title,image_id&page="
                +this.state.page+"&limit=99&sort[date_end][order]=asc")

                .then((response) => {
                    const tmp: any[] = []

                    if (response.data) {
                        response.data.data.map( (item:Item) => (
                            tmp.push(item)
                        ))
                        this.setState({
                            items:tmp,
                            loaded: true,
                            count:0
                        })
                    }
                })
    }


    componentDidMount() {
        this.fetchData();
    }

    componentDidUpdate() {
        this.fetchData();
    }



    handleOnError(event:any) {
        // alert(event.target);
        // alert(event.target.style);
        // alert(event.target.style);
        // let curr = document.getElementById("helper") as HTMLImageElement;
        // alert(curr);

        // curr.src= 'https://www.nutraingredients-usa.com/var/wrbm_gb_food_pharma/storage/images/9/4/5/8/218549-6-eng-GB/Akay-Flavours-Aromatics-Pvt.-Ltd2.jpg';
    }

    getImage(url:any, id:any) {
        // if id is empty
        if(id === null) {
            return 'https://www.nutraingredients-usa.com/var/wrbm_gb_food_pharma/storage/images/9/4/5/8/218549-6-eng-GB/Akay-Flavours-Aromatics-Pvt.-Ltd2.jpg'
        }
        if(id.length === 0) {
            return 'https://www.nutraingredients-usa.com/var/wrbm_gb_food_pharma/storage/images/9/4/5/8/218549-6-eng-GB/Akay-Flavours-Aromatics-Pvt.-Ltd2.jpg'
        }
        return url + "/" + id +"/full/843,/0/default.jpg";
    }

    render() {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        } 
        else if (this.state.items.length === 0) {
            return <div> <h1> Loading2 </h1> </div> ;
        }
        else {
            return (
                <div className = "App">
                    <h1 className="title"> Artwork List </h1>  
                    <div className="box2">
                    <button onClick = {()=>{this.setState({start:-1,end:1900})}} className="buttonsize2">Before 1900</button>
                    <button onClick = {()=>(this.setState({start:1900,end:1950}))} className="buttonsize2">1900-1950</button>
                    <button onClick = {()=>(this.setState({start:1950,end:2000}))} className="buttonsize2">1950-2000</button>
                    <button onClick = {()=>(this.setState({start:2000,end:3000}))} className="buttonsize2">2000-present</button>
                    </div>

                    <div className="box">
                    {   
                    // this.state.items.length
                    this.state.items.map((item:any) => ( 
                        <div className ="egg">
                            {/* <div className="word">{item.title}</div> */}
                            <img src={this.getImage("https://www.artic.edu/iiif/2/", item.image_id)} className="helper" id ="helper" alt ="source unavailable"
                            onError={()=>this.handleOnError(item.image_id)} >
                            </img>
                            <br></br>
                            <Link to ={"/gallery/"+item.id} className ="word6">{item.title}</Link>
                            </div>
                        ))
                    }

                    </div>

                    <div className="box2">
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page-1}))} className="buttonsize">Last page</button>
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page+1}))} className="buttonsize">Next page</button>
                    </div>


                </div>
            );
        }
    }
}

export default Results;
