import './App.css';
import './galleryView.css';
import './detail.css';
import axios from 'axios';
import { useLocation } from 'react-router-dom'

interface Item {
    id: number,
    image_id: string,
    date_display:number,
    title: string
}

export default function Detail(s:any) {
    const location = useLocation();
    const loc_cut = location.pathname.substring(9);
    const tmp: any[] = []
    const [state, setState] = useState([] as any)

    useEffect(() => {
        const url = "https://api.artic.edu/api/v1/artworks/search?query[term][id]="+loc_cut+"&fields=title,artwork_type_id,id, image_id";
        const fetchData = async () => {
            const response = await fetch(url);
            const json = await response.json();
            // json.data.map((item:Item) => (
            //                 tmp.push(item)
            //             ))
            console.log(json.data[0]);
            setState(json.data[0]);
        };
        fetchData();

    }, []);

    console.log(state);

    return(
        <div>
            <div className ="word">{state.title}</div>

            <img src={"https://www.artic.edu/iiif/2/"+ state.image_id+"/full/843,/0/default.jpg"} ></img>
        </div>
    )

};
