import React, { useState,useEffect } from 'react';
import './App.css';
import './galleryView.css';
import './detail.css';
import axios from 'axios';
import { useLocation } from 'react-router-dom'

interface Item {
    id: number,
    image_id: string,
    date_display:number,
    title: string
}

export default function Detail(s:any) {
    const location = useLocation();
    const loc_cut = location.pathname.substring(9);
    const [state, setState] = useState([] as any)

    useEffect(() => {
        const fetchArt = async () => {
            const response = await fetch("https://api.artic.edu/api/v1/artworks/search?query[term][id]="+loc_cut+"&fields=title,artwork_type_title,artist_title,department_title,department_title,id,image_id,place_of_origin,dimensions,medium_display,credit_line");
            const json = await response.json();
            setState(json.data[0]);
        };
        fetchArt();
    }, []);

    console.log(state);

    return(
        <div className='gridbox'>

            <img src={"https://www.artic.edu/iiif/2/"+ state.image_id+"/full/843,/0/default.jpg"} ></img>
            <div className ="word">Title: {state.title}</div>
            <div className ="word">Author : {state.artist_title}</div>
            <div className ="word">Type : {state.artwork_type_title}</div>
            <div className ="word">Department : {state.department_title}</div>
            <div className ="word">Place : {state.place_of_origin}</div>
            <div className ="word">Dimensions : {state.dimensions}</div>
            <div className ="word">Medium : {state.medium_display}</div>
            <div className ="word">Credit : {state.credit_line}</div>
        </div>
    )

};
