import React from "react";
import './App.css';
import './galleryView.css';
import axios from 'axios';
import { isTemplateSpan } from "typescript";

interface Image {
    id: number,
    image_id: string,
    title: string,
    _score:number
}


const api = axios.create({
    baseURL: 'https://api.artic.edu/api/v1/'
})

class Results extends React.Component<{}, { items: Array<any>, loaded:boolean, count:number,page:number}> {
    constructor(props:any) {
        super(props);
        this.state = {
            items: [],
            loaded: false,
            count:0,
            page:1
        }
    }

    fetchData(pageNum:any) {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        }
        axios.get("https://api.artic.edu/api/v1/artworks?fields=id,title,image_id&page="+pageNum+"&limit=33")
            .then((response) => {
                const tmp: string[] = []
                if (response.data) {
                    response.data.data.map( (image:Image) => (
                        tmp.push(`https://www.artic.edu/iiif/2/${image.image_id}/full/843,/0/default.jpg`)
                    ))
                    this.setState({

                        items:tmp,
                        loaded: true,
                        count:0
                    })
                }

                // this.setState({
                //     items: response.data,
                //     loaded: true,
                //     count:0
                // });
            })
    }

    componentDidMount() {
        // api.get(`https://api.artic.edu/api/v1/artworks/search?fields=id,title,image_id&page=${this.state.page}`)
        //     .then((response) => {
        //         this.setState({
        //             items: response.data,
        //             loaded: true,
        //             count:0
        //         });
        //     })
        this.fetchData(1);
    }
    componentWillUnmount() {
        // All clean up should come to here
    }

    // componentDidUpdate() {
    //     // After any update
    //     // this.setState({loaded:false})
    //     if (!this.state.loaded) {
    //         return <div> <h1> Loading </h1> </div> ;
    //     }
    //     this.fetchData(this.state.page)
    //     // alert(this.state.page)

    // }



    onError(event:any) {
        const target = event.target as HTMLImageElement;
        target.style.display= 'none';
    }

    getImage(url:any, id:any) {
        // if id is empty
        // if(id === null) {
        //     return ''
        // }
        return url + "/" + id +"/full/843,/0/default.jpg";
    }

    render() {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        } 
        else if (this.state.items.length === 0) {
            return <div> <h1> Loading2 </h1> </div> ;
        }
        else {
            return (
                <div className = "App">

                    <h1 className="title"> Artwork List </h1>  
                    <div className="box">
                    {   
                    this.state.items.length
                    // this.state.items.map((item:any) => ( 
                    //     <ol key = { item.id } className ="egg">
                    //         <div className="word">{item.title}</div>
                    //         <img src={this.getImage("https://www.artic.edu/iiif/2/", item.image_id)} className="helper" alt ="source unavailable"/>
                    //         </ol>
                    //     ))
            }

                    </div>
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page-1}))}>Last page</button>
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page+1}))}>Next page</button>
                </div>
            );
        }
    }
}

export default Results;
