import React from "react";
import './App.css';
import './galleryView.css';
import axios from 'axios';
import { isTemplateSpan } from "typescript";

interface Item {
    id: number,
    image_id: string,
    date_display:number,
    title: string
}


const api = axios.create({
    baseURL: 'https://api.artic.edu/api/v1/'
})

class Results extends React.Component<{}, { items: Array<any>, loaded:boolean, count:number,page:number,year:number}> {
    constructor(props:any) {
        super(props);
        this.state = {
            items: [],
            loaded: false,
            count:0,
            page:1,
            year:2000
        }
    }

    fetchData() {
        axios.get("https://api.artic.edu/api/v1/artworks?fields=id,title,image_id&page="+this.state.page+"&limit=33&query[term][date_display]<"+"1950")
            .then((response) => {
                const tmp: string[] = []

                if (response.data) {
                    response.data.data.map( (item:Item) => (
                        tmp.push(item.image_id)

                    ))
                    this.setState({
                        items:tmp,
                        loaded: true,
                        count:0
                    })
                }
            })
    }

    componentDidMount() {
        this.fetchData();
    }
    componentWillUnmount() {
        // All clean up should come to here
    }

    componentDidUpdate() {
        this.fetchData()
    }



    onError(event:any) {
        const target = event.target as HTMLImageElement;
        target.style.display= 'none';
    }

    getImage(url:any, id:any) {
        // if id is empty
        // if(id === null) {
        //     return ''
        // }
        return url + "/" + id +"/full/843,/0/default.jpg";
    }

    render() {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        } 
        else if (this.state.items.length === 0) {
            return <div> <h1> Loading2 </h1> </div> ;
        }
        else {
            return (
                <div className = "App">

                    <h1 className="title"> Artwork List </h1>  
                    <div className="box2">
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page+1}))} className="buttonsize2">Before 1900</button>
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page+1}))} className="buttonsize2"> 1900-1950</button>
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page+1}))} className="buttonsize2"> 1920-2000</button>
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page+1}))} className="buttonsize2"> 2000-present</button>
                    </div>

                    <div className="box">
                    {   
                    // this.state.items.length
                    this.state.items.map((item:any) => ( 
                        <ol key = { item } className ="egg">
                            {/* <div className="word">{item.title}</div> */}
                            <img src={this.getImage("https://www.artic.edu/iiif/2/", item)} className="helper" alt ="source unavailable"/>
                            </ol>
                        ))
                    }

                    </div>
                    <div className="box2">
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page-1}))} className="buttonsize">Last page</button>
                    <button onClick = {()=>(this.setState({loaded:false, page:this.state.page+1}))} className="buttonsize">Next page</button>
                    </div>

                </div>
            );
        }
    }
}

export default Results;
