import React, { useState,useEffect } from 'react';
import './App.css';
import './galleryView.css';
import './detail.css';

import { useLocation } from 'react-router-dom'
import axios from 'axios';


export default function Detail(s:any) {
    const location = useLocation();
    const loc_cut = location.pathname.substring(9);
    const [state, setState] = useState([] as any)
    const [loc, setLoc] = useState(loc_cut)

    useEffect(() => {


        return () => {
            axios.get("https://api.artic.edu/api/v1/artworks/search?query[term][id]="+loc+"&fields=title,artwork_type_title,artist_title,department_title,department_title,id,image_id,place_of_origin,dimensions,medium_display,credit_line")
            .then((response) => {
                setState(response.data.data[0])
            })
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    console.log(state);

    let handleClick = function() {
        alert("sd");
        setLoc("15456");

    };

    console.log(state);

    return(
        <div>
            <div className='gridbox'>
                <div className='grid-container'>
                <img src={"https://www.artic.edu/iiif/2/"+ state.image_id+"/full/843,/0/default.jpg" } alt="not available"></img>
                </div>
                <div className='grid-container'>
                <div className ="word">Title: {state.title}</div>
                <div className ="word">Author : {state.artist_title}</div>
                <div className ="word">Type : {state.artwork_type_title}</div>
                <div className ="word">Department : {state.department_title}</div>
                <div className ="word">Place : {state.place_of_origin}</div>
                <div className ="word">Dimensions : {state.dimensions}</div>
                <div className ="word">Medium : {state.medium_display}</div>
                <div className ="word">Credit : {state.credit_line}</div>
                </div>
            </div>
            <div className="box2">
            <button className="buttonsize" onClick ={handleClick}>Last page</button>
            <button className="buttonsize"  onClick ={handleClick}>Next page</button>
            </div>

        </div>

    )

};
