import React from "react";
import './App.css';
import './galleryView.css';
import axios from 'axios';

interface Item {
    id: number,
    image_id: string,
    title: string
}

// const createElement = React.createElement('hh', {className: "a"}, "Yes")
class SearchBar extends React.Component<{}, { items: Array<Item>,loaded:boolean, count:number,page:number,value:string,curr:string}> {
    constructor(props:any) {
        super(props);
        this.state = {
            page:1,
            value: '',
            items: [],
            loaded: false,
            count:0,
            curr:''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    fetchData() {
        axios.get("https://api.artic.edu/api/v1/artworks/search?q="+this.state.value+"&fields=id,title,image_id&size=33&sort=id")
            .then((response) => {
                const tmp: any[] = []

                if (response.data) {
                    response.data.data.map( (item:Item) => (
                        tmp.push(item)
                    ))
                    this.setState({
                        items:tmp,
                        loaded: true,
                        count:0
                    })
                }
            })
    }



    componentDidMount() {
        this.fetchData()
    }

    componentDidUpdate() {
        this.fetchData()        
    }


    getImage(url:any, id:any) {
        // if id is empty
        if(id === null) {
            return 'https://www.nutraingredients-usa.com/var/wrbm_gb_food_pharma/storage/images/9/4/5/8/218549-6-eng-GB/Akay-Flavours-Aromatics-Pvt.-Ltd2.jpg'
        }
        return url + "/" + id +"/full/843,/0/default.jpg";
    }

    handleChange(event:any) {
        this.setState({value: event.target.value})
    }

    handleSubmit(event:any) {
        alert(this.state.value)
    }

    render() {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        } else { return (
            <div className = "App">
            <form onSubmit = {this.handleSubmit}>
            <label>
                <div className="title">Search Bar:</div>

                <input
                    type = "text"
                    value = {this.state.value}
                    onChange = {this.handleChange} />
            </label>
            <input type ="submit" value ="Clear" />

            </form>
            <h1 className="title"> Artwork List </h1>  
            <div className="box">
            {   
                this.state.items.map((item:any) => ( 
                    <div className ="egg">
                        <div className="word">{item.title}</div>
                        <img src={this.getImage("https://www.artic.edu/iiif/2/", item.image_id)} className="helper" alt ="source unavailable" 

                        />
                        </div>
                    ))

            }
            </div></div>
        )
        }

    }
}

export default SearchBar;
