import React from "react";
import './App.css';
import './showResult.css';
import axios from 'axios';

// const createElement = React.createElement('hh', {className: "a"}, "Yes")
class SearchBar extends React.Component<{}, { items: [],loaded:boolean, count:number,page:number,value:string,curr:string}> {
    constructor(props:any) {
        super(props);
        this.state = {
            page:1,
            value: '',
            items: [],
            loaded: false,
            count:0,
            curr:''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        axios.get("https://api.artic.edu/api/v1/artworks/search?fields=id,title,image_id")
            .then((response) => {
                this.setState({
                    items: response.data,
                    loaded: true,
                    count:0
                });
            })
    }

    componentDidUpdate() {
        // After any update
        // this.setState({loaded:false})
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        }
        this.fetchData(this.state.page)
        // alert(this.state.page)

    }

    fetchData(pageNum:any) {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        }
        axios.get("https://api.artic.edu/api/v1/artworks/search?q="+this.state.value+"&fields=id,title,image_id&size=33&sort=id")
            .then((response) => {
                this.setState({
                    items: response.data,
                    loaded: true,
                    count:0
                });
            })
    }


    getImage(url:any, id:any) {
        // if id is empty
        if(id === null) {
            return 'https://www.artic.edu/iiif/2/005de1b7-6589-f1d9-e0ca-91bfca310c59/full/843,/0/default.jpg'
        }
        return url + "/" + id +"/full/843,/0/default.jpg";
    }

    handleChange(event:any) {
        this.setState({value: event.target.value})
    }

    handleSubmit(event:any) {
        alert(this.state.value)
        // fetch("https://api.artic.edu/api/v1/artworks")
        // .then((response) => response.json())
        // .then((json) => {
        //     this.setState({
        //         items: json,
        //         loaded: true,
        //         count:0
        //     });
        // })
    }

    render() {
        if (!this.state.loaded) {
            return <div> <h1> Loading </h1> </div> ;
        } else { return (


            <div className = "App">
            <form onSubmit = {this.handleSubmit}>
            <label>
                <h3>Search by name</h3>
                <p/>
                <input
                    type = "text"
                    value = {this.state.value}
                    onChange = {this.handleChange} />
            </label>
            <input type ="submit" value ="Clear" />

                {/* <h1> Artwork List </h1>   */}
            </form>

            <h1 className="title"> Artwork List </h1>  
            <div className="box">
            {   
                this.state.items.map((item:any) => ( 
                    <ol key = { item.id } className ="egg">
                        {/* {"-"+item.image_id+"-"} */}
                        <div className="word">{item.title}</div>
                        <img src={this.getImage("https://www.artic.edu/iiif/2/", item.image_id)} className="helper" alt ="source unavailable" 
                                // onerror={({ currentTarget }) => {
                                //         currentTarget.onerror = null; 
                                //         currentTarget.src="https://www.artic.edu/iiif/2/005de1b7-6589-f1d9-e0ca-91bfca310c59/full/843,/0/default.jpg";}}
                        />
                        </ol>
                    // ()=>{
                    //     if(true) {
                    //         <ol key = { item.id } className ="egg">
                    //             <img src={this.getImage(this.state.items.config.iiif_url, item.image_id)} class="helper"/>
                    //             </ol>
                    //     }
                    // }
                    ))

            }
            </div></div>
        )
        }

    }
}

export default SearchBar;
