import React, { useState,useEffect } from 'react';
import './App.css';
import './galleryView.css';
import './detail.css';

import { useLocation, useNavigate } from 'react-router-dom'
import axios from 'axios';



export default function Detail(s:any) {
    const location = useLocation();
    const loc_cut = location.pathname.substring(9);
    const [state, setState] = useState([] as any)
    const [loc, setLoc] = useState(loc_cut)
    const [title, setTitle] = useState("")
    const [id_li, setId] = useState([] as any)


    const navigate = useNavigate();
    const prev_id = "153627";
    const next_id = "153627"



    useEffect(() => {
        let id_new  = id_li
        id_new.sort((a:any,b:any) =>a.id-b.id);
        console.log(id_new);
        setId(id_new);
    }, []);

    // console.log(id_li);


    useEffect(() => {
        const fetchArt = async () => {
            const response = await axios.get("https://api.artic.edu/api/v1/artworks/search?query[term][id]="+loc+"&fields=title,artwork_type_title,artist_title,department_title,department_title,id,image_id,place_of_origin,dimensions,medium_display,credit_line");
            setState(response.data.data[0]);
            let new_str = state.title.substring(200);
            navigate("/gallery/"+loc);
        };

        fetchArt();
        return () => {
            // console.log('Component will be unmount')
        }
    }, [loc]);

    useEffect(() => {
        let tmp: any[] = []

            axios.get(`https://api.artic.edu/api/v1/artworks/search?q=${title}&fields=id&page=1&limit=100`)
                    .then((response) => {
                        if (response.data) {
                            tmp = response.data.data;
                            setId(tmp)

                        }
                    })
                // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [title]);

    // console.log(state);

    let handleClick1 = function() {
        setLoc(prev_id);
        alert(loc);

    };

    let handleClick2 = function() {
        setLoc(next_id);
        alert(loc);

    };


    // console.log(state);
    // console.log(id_li.length);
    let source = `https://www.artic.edu/iiif/2/${state.image_id}/full/843,/0/default.jpg`
    return(
        <div>
            <div className='gridbox'>
                <div className='grid-container'>
                <img src={source} alt="not available"></img>
                </div>
                <div className='grid-container'>
                <div className ="word">Title: {state.title}</div>
                <br></br>
                <div className ="word">Author : {state.artist_title}</div>
                <br></br>
                <div className ="word">Type : {state.artwork_type_title}</div>
                <br></br>
                <div className ="word">Department : {state.department_title}</div>
                <br></br>
                <div className ="word">Place : {state.place_of_origin}</div>
                <br></br>
                <div className ="word">Dimensions : {state.dimensions}</div>
                <br></br>
                <div className ="word">Medium : {state.medium_display}</div>
                <br></br>
                <div className ="word">Credit : {state.credit_line}</div>
                </div>
            </div>
            <div className="box2">
            <button className="buttonsize" onClick ={handleClick1}>Last Artwork</button>
            <button className="buttonsize"  onClick ={handleClick2}>Next Artwork</button>
            </div>

        </div>
    )

};
